mapper.update:
UPDATE QNA_BOARD
   SET QNA_ORDER = QNA_ORDER + 1
 WHERE QNA_GROUP = #{qnaGroup}
   AND QNA_ORDER >= #{qnaOrder}

UPDATE QNA_BOARD
SET QNA_TITLE = #{qnaTitle},
    QNA_CONTENT = #{qnaContent}
WHERE QNA_SEQ = #{qnaSeq}

mapper.select:
SELECT COUNT(QNA_SEQ) AS CNT
   FROM QNA_BOARD
  WHERE QNA_PARENT = #{value}

mapper.update:
UPDATE QNA_BOARD
   SET ANS_STATUS = #{ansStatus}
 WHERE QNA_SEQ = #{qnaSeq}

UPDATE QNA_BOARD_ANS
SET  ANS_TITLE = #{ansTitle},
     ANS_CONTENT = #{ansContent}
WHERE QNA_SEQ = #{qnaSeq}

mapper.select:
SELECT COUNT(QNA_SEQ) AS CNT
   FROM QNA_BOARD_ANS
  WHERE QNA_SEQ = #{value}

SELECT QNA_SEQ, QNA_TITLE, USER_ID, REG_DATE, QNA_GROUP, QNA_ORDER, QNA_INDENT, QNA_PARENT, QNA_CONTENT, ANS_STATUS FROM(SELECT ROWNUM AS RNUM, QNA_SEQ, QNA_TITLE, USER_ID, REG_DATE, QNA_GROUP, QNA_ORDER, QNA_INDENT, QNA_PARENT, QNA_CONTENT, ANS_STATUS FROM(SELECT QNA_SEQ, NVL(QNA_TITLE, '') AS QNA_TITLE, NVL(USER_ID, '') AS USER_ID, NVL(TO_CHAR(REG_DATE, 'YYYY.MM.DD'), '') AS REG_DATE, NVL(QNA_GROUP, '') AS QNA_GROUP, NVL(QNA_ORDER, '') AS QNA_ORDER, NVL(QNA_INDENT, '') AS QNA_INDENT, NVL(QNA_PARENT, '') AS QNA_PARENT, NVL(QNA_CONTENT, '') AS QNA_CONTENT, NVL(ANS_STATUS, '') AS ANS_STATUS FROM QNA_BOARD WHERE USER_ID = #{userId}
AND QNA_TITLE LIKE '%' || #{searchValue} || '%'
ORDER BY REG_DATE DESC
ORDER BY REG_DATE ASC
)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
