mapper.select:
SELECT USER_ID, IRDNT_NAME, FRIDGE_IRDNT_AMOUNT FROM( SELECT ROWNUM AS RNM, USER_ID AS USER_ID, IRDNT_NAME AS IRDNT_NAME, FRIDGE_IRDNT_AMOUNT AS FRIDGE_IRDNT_AMOUNT FROM ( SELECT USER_ID , IRDNT_NAME , FRIDGE_IRDNT_AMOUNT FROM MY_FRIDGE WHERE USER_ID=#{userId} AND FRIDGE_IRDNT_AMOUNT > 0
AND IRDNT_NAME LIKE '%' || #{searchValue} ||'%'
ORDER BY IRDNT_NAME)) WHERE RNM >= #{startRow} AND RNM <= #{endRow}

mapper.delete:
		DELETE 
		FROM MY_FRIDGE
		WHERE
        	USER_ID = #{userId}
    	AND IRDNT_NAME = #{irdntName}

mapper.update:
UPDATE MY_FRIDGE
SET
    FRIDGE_IRDNT_AMOUNT = #{fridgeIrdntAmount}
WHERE
        USER_ID = #{userId}
        AND IRDNT_NAME = #{irdntName}

mapper.select:
SELECT IRDNT_NAME, IRDNT_IMAGE FROM( SELECT ROWNUM AS RNM, IRDNT_NAME, IRDNT_IMAGE FROM( SELECT IRDNT_NAME AS IRDNT_NAME, IRDNT_IMAGE AS IRDNT_IMAGE FROM IRDNT_INFO WHERE 1=1
AND IRDNT_NAME LIKE '%' || #{searchValue} ||'%'
ORDER BY IRDNT_NAME ASC)) WHERE RNM >= #{startRow} AND RNM <= #{endRow}

	SELECT COUNT(IRDNT_NAME)
FROM MY_FRIDGE
WHERE 
USER_ID =#{userId}
AND IRDNT_NAME = #{irdntName}
