mapper.select:
SELECT NATION_NM, RECIPE_CODE, RECIPE_NAME, RECIPE_QNT, RECIPE_TIME, NATION_CODE, FOOD_CODE, REG_DATE, RECIPE_INTRO, RECIPE_IMAGE, USER_ID, BBS_READ_CNT FROM (SELECT ROWNUM RNUM, NATION_NM, RECIPE_CODE, RECIPE_NAME, RECIPE_QNT, RECIPE_TIME, NATION_CODE, FOOD_CODE, REG_DATE, RECIPE_INTRO, RECIPE_IMAGE, USER_ID, BBS_READ_CNT FROM(SELECT A.NATION_CODE, NVL(B.NATION_NM, '') AS NATION_NM, NVL(A.RECIPE_CODE, 0) AS RECIPE_CODE, NVL(A.RECIPE_NAME, '') AS RECIPE_NAME, NVL(A.RECIPE_QNT, '') AS RECIPE_QNT, NVL(A.RECIPE_TIME, '') AS RECIPE_TIME, NVL(A.FOOD_CODE, 0) AS FOOD_CODE, NVL(TO_CHAR(A.REG_DATE, 'YYYY.MM.DD HH24:MI:SS'), '') AS REG_DATE, NVL(A.RECIPE_INTRO, '') AS RECIPE_INTRO, NVL(A.RECIPE_IMAGE, '') AS RECIPE_IMAGE, NVL(A.USER_ID, '')AS USER_ID, NVL(A.BBS_READ_CNT, 0)AS BBS_READ_CNT FROM RECIPE_INFO A , CATEGORY_NATION B WHERE 1 = 1
AND A.NATION_CODE = #{searchType}
AND A.RECIPE_NAME LIKE '%' || #{searchValue} ||'%'
AND A.NATION_CODE = B.NATION_CODE ORDER BY A.RECIPE_CODE DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE RECIPE_INFO
   SET BBS_READ_CNT = BBS_READ_CNT + 1
 WHERE RECIPE_CODE = ${value}

mapper.select:
SELECT COUNT(REPLY_CONTENT) AS TOTAL_COUNT
  FROM RECIPE_REPLY
 WHERE RECIPE_CODE = #{value}

SELECT COUNT(RECIPE_CODE) AS TOTAL_COUNT
 FROM RECIPE_RCM_CNT
 WHERE RECIPE_CODE = #{recipeCode}
   AND USER_ID = #{userId}
   AND RCM_CHECK = 1

SELECT COUNT(USER_ID) AS RCM_COUNT
  FROM RECIPE_RCM_CNT
 WHERE RECIPE_CODE = #{recipeCode}
   AND USER_ID = #{userId}

SELECT RECIPE_CODE, RECIPE_NAME, RECIPE_QNT, RECIPE_TIME, NATION_CODE, FOOD_CODE, REG_DATE, RECIPE_INTRO, RECIPE_IMAGE, USER_ID, BBS_READ_CNT FROM (SELECT ROWNUM AS RNUM, RECIPE_CODE, RECIPE_NAME, RECIPE_QNT, RECIPE_TIME, NATION_CODE, FOOD_CODE, REG_DATE, RECIPE_INTRO, RECIPE_IMAGE, USER_ID, BBS_READ_CNT FROM(SELECT RECIPE_CODE, NVL(RECIPE_NAME, '') AS RECIPE_NAME, NVL(RECIPE_QNT, '') AS RECIPE_QNT, NVL(RECIPE_TIME, '') AS RECIPE_TIME, NVL(NATION_CODE, '') AS NATION_CODE, NVL(FOOD_CODE, '') AS FOOD_CODE, NVL(TO_CHAR(REG_DATE, 'YYYY.MM.DD HH24:MI:SS'), '') AS REG_DATE, NVL(RECIPE_INTRO, '') AS RECIPE_INTRO, NVL(RECIPE_IMAGE, '') AS RECIPE_IMAGE, NVL(USER_ID, '') AS USER_ID, NVL(BBS_READ_CNT, '') AS BBS_READ_CNT FROM RECIPE_INFO WHERE USER_ID = #{userId}
AND RECIPE_NAME LIKE '%' || #{recipeName} || '%'
AND DBMS_LOB.INSTR(RECIPE_INTRO, #{recipeIntro}) > 0
ORDER BY REG_DATE DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT COUNT(RECIPE_CODE) AS TOTAL_COUNT FROM RECIPE_INFO WHERE USER_ID = #{userId}
AND RECIPE_NAME LIKE '%' || #{recipeName} || '%'
AND DBMS_LOB.INSTR(RECIPE_INTRO, #{recipeIntro}) > 0

mapper.delete:
DELETE FROM RECIPE_RCM_CNT
 WHERE USER_ID = #{userId}
   AND RECIPE_CODE = #{recipeCode}

mapper.update:
UPDATE RECIPE_INFO
   SET RCM_CNT = RCM_CNT + 1
 WHERE RECIPE_CODE = #{recipeCode}

mapper.delete:
UPDATE RECIPE_INFO
   SET RCM_CNT = RCM_CNT - 1
 WHERE RECIPE_CODE = #{recipeCode}

mapper.insert:
INSERT INTO USER_BOOKMARK (
    USER_ID,
    RECIPE_CODE
) VALUES (
    #{userId},
    #{recipeCode}
)

mapper.select:
SELECT COUNT(USER_ID)
  FROM USER_BOOKMARK
 WHERE USER_ID = #{userId}
   AND RECIPE_CODE = #{recipeCode}

mapper.delete:
DELETE 
  FROM USER_BOOKMARK
 WHERE USER_ID = #{userId}
   AND RECIPE_CODE = #{recipeCode}
